import { useNavigate } from "react-router-dom";
import setaCirculoEsquerda from '../imagens/setaCirculoEsquerda.svg';
import '../estilizacao/CadastroEstilo.css'; // Corrigido: só precisa importar o caminho

export default function EsqueciSenhaModal() {
    const navigate = useNavigate();

    return (
        <div className="modal-overlay">
            <div style={{
                backgroundColor: 'white',
                width: 400,
                height: 450,
                paddingTop: 40,
                borderRadius: 10,
                position: 'relative'
            }}>
                <img
                    src={setaCirculoEsquerda}
                    onClick={() => navigate('/login')}
                    alt="Voltar"
                    style={{
                        position: 'absolute',
                        top: 10,
                        left: 10,
                        width: 30,
                        height: 30,
                        cursor: 'pointer'
                    }}
                />
                <h1>Redefinição de senha!</h1>
                <p>Informe um email e enviaremos um link <br /> para recuperação da sua senha.</p>
                <div style={{ display: 'flex', flexDirection: 'column', marginBottom: 25 }}>
                    <label><b>Email</b></label>
                    <input style={{ borderRadius: 3 }} />
                </div>
                <button style={{
                    backgroundColor: '#6A0DAD',
                    color: '#FFFFFF',
                    width: 250,
                    padding: '10px',
                    borderRadius: 5,
                    border: 'none',
                    cursor: 'pointer'
                }}>
                    Enviar link de recuperação.
                </button>
            </div>
        </div>
    );
}
