import { useNavigate } from "react-router-dom"
import setaCirculoEsquerda from '../imagens/setaCirculoEsquerda.svg'
import api, { requisicaoParaTrocarDeSenha } from '../utils/api.js'
import EsqueciSenha from '../estilizacao/CadastroEstilo.css'
import { useState } from "react";
export default function EsqueciSenhaModal(){

    const [usuario, setUsuario] = useState('');
    const [erro, setErro] = useState('');

    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (usuario === '') {
            setErro('Preencha o campo email.');
            return;
        }
        setErro('');
    };

    async function enviandoEmailParaReceberCodigoTrocarSenha(){
        try{
            await requisicaoParaTrocarDeSenha(usuario)
            alert('Email enviado com sucesso!');

        }catch{
            console.log('erro >>>>>>>>>>>> ');
            alert('Falha ao enviar email');
        }
    }

return (
  <div className="redefinir-senha-container">
    <div className="redefinir-senha-box">
      <img
        src={setaCirculoEsquerda}
        onClick={() => navigate('/login')}
        style={{
          position: 'absolute',
          top: 10,
          left: 10,
          width: 30,
          height: 30,
          cursor: 'pointer'
        }}
        alt="Voltar"
      />

      <h1 style={{ color: '#000' }}>Redefinição de senha!</h1>
      <p style={{ textAlign: 'center', color: '#333', marginBottom: 20 }}>
        Informe um email e enviaremos um link <br /> para recuperação da sua senha.
      </p>

      <form onSubmit={handleSubmit} style={{ width: '100%' }}>
        <div style={{ display: 'flex', flexDirection: 'column', marginBottom: 20 }}>
          <label style={{ color: '#000', marginBottom: 5 }}><b>Email</b></label>
          <input
            type="email"
            value={usuario}
            onChange={e => setUsuario(e.target.value)}
            required
            style={{
              padding: 10,
              borderRadius: 5,
              fontSize: 15,
              border: '1px solid #ccc',
              outline: 'none'
            }}
          />
          {erro && <div style={{ color: 'red', marginTop: 10 }}>{erro}</div>}
        </div>
        <button
          onClick={enviandoEmailParaReceberCodigoTrocarSenha}
          type="submit"
          style={{
            backgroundColor: '#9E184B',
            color: '#FFFFFF',
            width: '100%',
            padding: '10px',
            borderRadius: 5,
            border: 'none',
            cursor: 'pointer',
            fontSize: 16
          }}
        >
          Enviar link de recuperação.
        </button>
      </form>
    </div>
  </div>
)



}
