import React from 'react';

function Home() {
    return (
        <div style={{
            minHeight: '100vh',
            background: 'linear-gradient(135deg, #1e3c72 0%, #2a5298 100%)',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center'
        }}>
            <h1 style={{ color: 'white', fontSize: '3rem', marginBottom: '1rem' }}>
                Bem-vindo à Casa de Apostas
            </h1>
            <p style={{ color: '#e0e0e0', fontSize: '1.2rem', marginBottom: '2rem' }}>
                Faça suas apostas nos melhores eventos esportivos!
            </p>
            <button style={{
                padding: '1rem 2rem',
                fontSize: '1.1rem',
                background: '#ffb300',
                color: '#222',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontWeight: 'bold',
                boxShadow: '0 2px 8px rgba(0,0,0,0.2)'
            }}>
                Ver apostas
            </button>
        </div>
    );
}

export default Home;
