import React from 'react';

function Minas() {
    return (
        <div style={{
            minHeight: '100vh',
            background: '#222',
            color: 'white',
            padding: '2rem',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center'
        }}>
            <h2>Bem-vindo, Usuário!</h2>
            <p>Saldo: <strong>R$ 500,00</strong></p>
            <h3>Jogo das Minas</h3>
            <p>
                Escolha as casas e tente evitar as minas! Quanto mais casas seguras você abrir, maior o prêmio.
            </p>
            <button style={{
                marginTop: '1.5rem',
                padding: '1rem 2rem',
                background: '#ffb300',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer',
                fontWeight: 'bold',
                fontSize: '1.1rem'
            }}>
                Iniciar Jogo
            </button>
        </div>
    );
}

export default Minas;
