import { useState, useEffect } from 'react';
import '../estilizacao/TelaJogo.css';
import {enviandoCaixaEscolhida} from '../utils/api'
import { useParams } from 'react-router-dom';
import diamante from '../imagens/diamante.png'
import bomba from '../imagens/bomba.png'

export default function TelaJogo() {
    const { id } = useParams();
    const [caixa_escolhida, setCaixa_escolhida] = useState("");
    const [resultados, setResultados] = useState({});

    async function enviandoCaixaEscolhidaPeloUsuario(index) {
        setCaixa_escolhida(index);
        try {
            await enviandoCaixaEscolhida(index, id);
            console.log('Caixa enviada com sucesso' + enviandoCaixaEscolhida);
            const response = await fetch(`/api/caixa/${index}`);
            const resultado = await response.text();
            console.log('resultado', resultado)
              setResultados((prev) => ({
                ...prev,
                [index]: resultado,
            }));
        } catch (err) {
            console.error('Erro enviando caixa escolhida:', err);
        }
    }

    return (
        <div className="tela_jogo">
            <div className="container_botoes">
                {Array.from({ length: 25 }).map((_, index) => (
                    <button
                        key={index}
                        id={`btn-${index}`}
                        value={index}
                        onClick={() => enviandoCaixaEscolhidaPeloUsuario(index)}
                        >{resultados[index] === "DIAMANTE" && <img src={diamante} alt="DIAMANTE" />}{resultados[index] === "BOMBA" && <img src={bomba} alt="BOMBA" />}
                    </button>
                ))}
            </div>
            <button id='parar'>Parar</button>
        </div>
    );
}
