import React, { useState } from 'react';
import api, { fazerLogin } from '../utils/api.js'
// eslint-disable-next-line
import LoginEstilo from '../estilizacao/LoginEstilo.css'
import logoYoxuTelaInicial from '../imagens/logoYoxuTelaLogin.png'
import Cadastro from './Cadastro.js'
import { Link, Navigate, useNavigate } from 'react-router-dom';

function Login() {
    const [usuario, setUsuario] = useState('');
    const [senha, setSenha] = useState('');
    const [erro, setErro] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (usuario === '' || senha === '') {
            setErro('Preencha todos os campos.');
            return;
        }
        setErro('');
        alert('Login realizado com sucesso!');
    };

    async function enviandoDadosParaFazerLogin(){
        try{
            console.log('teste')
            await fazerLogin(usuario, senha)
            console.log('enviado >>> ', fazerLogin);
        }catch{
            console.log('erro >>>>>>> ');

        }
    }

    const navigate = useNavigate();

return (
  <div className="login_container" >
    <span>Oi</span>
    <div className='div_login_pai'>
      <div className='div_login_filho'>
        <div className='div_login'>
          <img src={logoYoxuTelaInicial} alt='Imagem Youx' />
          <h1 style={{ color: 'black' }}>Login</h1>
        </div>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: 16, display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
            <label style={{ color: 'black' }}>E-mail:</label>
            <input
              type="email"
              value={usuario}
              onChange={e => setUsuario(e.target.value)}
              style={{ width: '50%', padding: 8, margin: 4, outline: 'none' }}
              required
            />
          </div>
          <div style={{ marginBottom: 16, display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
            <label style={{ color: 'black' }}>Senha:</label>
            <input
              type="password"
              value={senha}
              onChange={e => setSenha(e.target.value)}
              style={{ width: '50%', padding: 8, margin: 4, outline: 'none' }}
              required
            />
            <a style={{ fontSize: 15, color: 'black', alignItems: 'flex-end' }} href='#'>Esqueceu a senha?</a>
          </div>
          {erro && <div style={{ color: 'red', marginBottom: 16 }}>{erro}</div>}
          <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', alignItems: 'center' }}>
            <button type="submit" id='btn_entrar' onClick={enviandoDadosParaFazerLogin}>Entrar</button>
              <button id="btn_nova_conta" onClick={() => navigate("/cadastro")}>Nova conta</button>
          </div>
        </form>
      </div>
    </div>
  </div>
);

}

export default Login;
