import React from 'react';

function Dashboard() {
    const eventos = [
        { id: 1, nome: 'Flamengo x Palmeiras', horario: '20:00', tipo: 'Futebol' },
        { id: 2, nome: 'Corinthians x Santos', horario: '22:00', tipo: 'Futebol' },
        { id: 3, nome: 'NBA: Lakers x Celtics', horario: '23:30', tipo: 'Basquete' },
    ];

    return (
        <div style={{
            minHeight: '100vh',
            background: '#222',
            color: 'white',
            padding: '2rem'
        }}>
            <h2>Bem-vindo, Usuário!</h2>
            <p>Saldo: <strong>R$ 500,00</strong></p>
            <h3>Eventos disponíveis para apostar:</h3>
            <ul>
                {eventos.map(evento => (
                    <li key={evento.id} style={{marginBottom: '1rem'}}>
                        <strong>{evento.nome}</strong> <br />
                        Horário: {evento.horario} <br />
                        Esporte: {evento.tipo} <br />
                        <button style={{
                            marginTop: '0.5rem',
                            padding: '0.5rem 1rem',
                            background: '#ffb300',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer'
                        }}>
                            Apostar
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Dashboard;
