import importCofrinho from '../imagens/cofrinho.png';
import sair from '../imagens/saindo.png';
import '../estilizacao/HomeEstilo.css';
import { useNavigate } from 'react-router-dom';

export default function Menu({nome, role, qntdDinheiro, qntdJogosFeitos, quantosGanho, quantosPerdeu, nomesDeTodosUsuarios, rota, children}) {
  const navigate = useNavigate();

  return (
    <div className="home-container" style={{ position: 'relative', minHeight: '100vh' }}>
      <img
        src={sair}
        alt="Voltar"
        className="btn-voltar"
        onClick={() => navigate(rota)}
      />

      <div className="sidebar">
        <span className='span_home' style={{ display: 'flex', alignItems: 'center', gap: '8px', fontSize: 20}}>
          <img src={importCofrinho} alt='cofrinho' style={{ width: 80 }} />
          <b>Saldo: R$ {qntdDinheiro}</b>
        </span>
      <h1 
  className='h1_home' 
  style={{
    marginTop: 20, 
    fontSize: 32, 
    fontWeight: '700', 
    color: '#2c3e50',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }}
>
  {role}
</h1>

<h2 
  className='h2_home' 
  style={{
    marginTop: 12, 
    fontSize: 26, 
    fontWeight: '600', 
    color: '#34495e',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }}
>
  {nome}
</h2>

<span 
  className='span_home' 
  style={{
    display: 'block',
    marginTop: 10, 
    fontSize: 20, 
    color: '#555',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }}
>
  Jogos feitos: {qntdJogosFeitos}
</span>
<span 
  className='span_home' 
  style={{
    display: 'block',
    marginTop: 6, 
    fontSize: 20, 
    color: '#555',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }}
>
  Quantos ganhou: {quantosGanho}
</span>
<span 
  className='span_home' 
  style={{
    display: 'block',
    marginTop: 6, 
    fontSize: 20, 
    color: '#555',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }}
>
  Quantos perdeu: {quantosPerdeu}
</span>
<span 
  className='span_home' 
  style={{
    display: 'block',
    marginTop: 6, 
    fontSize: 20, 
    color: '#27ae60', // verde suave para valor ganho
    fontWeight: '600',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }}
>
  Valor ganho: R$ 0,00
</span>

{role === "ADM" && (
  <>
    <span 
      className='span_home' 
      style={{
        display: 'block',
        marginTop: 18, 
        fontSize: 20, 
        fontWeight: '600', 
        color: '#2980b9',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      Lista usu√°rio:
    </span>

    <select 
      style={{
        marginTop: 10,
        fontSize: 18, 
        width: 180, 
        height: 45, 
        borderRadius: 8,
        border: '1.5px solid #2980b9',
        backgroundColor: '#ecf0f1',
        color: '#2c3e50',
        fontWeight: '500',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        cursor: 'not-allowed',
      }} 
      multiple 
      disabled 
      size={nomesDeTodosUsuarios.length}
    >
      {nomesDeTodosUsuarios.map((nome, i) => (
          <option key={i} value={nome} style={{padding: '6px'}}>
            {nome}
          </option>
      ))}
    </select>
  </>
)}

      </div>
        <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-30%, -50%)'}}>
            {children}
        </div>
    </div>
  );
}
