import { use, useState } from "react";
import InputDataNascimento from '../componentes/InputDataNascimento.js';
import api, { fazendoCadastro } from '../utils/api.js'
import CadastroEstilo from '../estilizacao/CadastroEstilo.css';
import logoYoxuTelaInicial from '../imagens/logoYoxuTelaLogin.png';

export default function Cadastro() {
    const [nome, setNome] = useState('');
    const [usuario, setUsuario] = useState('');
    const [dataDeNascimento, setDataDeNascimento] = useState('');
    const [senha, setSenha] = useState('');
    const [qntdDinheiro, setQntdDineheiro] = useState('');
    const [quantosGanho, setQuantosGanho] = useState(0);
    const [quantosPerdeu, setQuantosPerdeu] = useState(0);
    const [erro, setErro] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (usuario === '' || senha === '') {
            setErro('Preencha todos os campos.');
            return;
        }
        setErro('');
        alert('Login realizado com sucesso!');
    };

    async function fazendoCadastro(){
        try{
            await fazendoCadastro(nome, usuario, dataDeNascimento, senha, qntdDinheiro, quantosGanho, quantosPerdeu)
        }catch{

        }
    }

    return (
        <div className="container_cadastro">
            <div className="div_pai">
                <form onSubmit={handleSubmit}>
                    <div className="div_cadastro_imagem">
                        <img src={logoYoxuTelaInicial} alt="Imagem Youx" />
                        <h1 style={{ color: 'black' }}>Cadastro</h1>
                    </div>

                    <div className="container_nome">
                        <label>Nome:</label>
                        <input value={nome} onChange={e => setNome(e.target.value)} />
                        {console.log("nome >>>>>>>>>>>>" + nome)}
                    </div>

                    <div className="container_usuario">
                        <label>Usuario:</label>
                        <input value={usuario} onChange={e => setUsuario(e.target.value)} />
                        {console.log("usuario >>>>>>>>>>>>" + usuario)}
                    </div>

                    <div className="container_usuario">
                        <label>Data de Nascimento:</label>
                        <InputDataNascimento value={dataDeNascimento} onChange={setDataDeNascimento} />
                        {console.log("Data de Nascimento >>>>>>>>>>>>" + dataDeNascimento)}
                    </div>

                    <div className="container_usuario">
                        <label>Senha:</label>
                        <input value={senha} onChange={e => setSenha(e.target.value)} />
                        {console.log("Senha >>>>>>>>>>>>" + senha)}
                    </div>

                    <div className="container_usuario">
                        <label>Dinheiro inicial:</label>
                        <input value={qntdDinheiro} onChange={e => setQntdDineheiro(e.target.value)} />
                        {console.log("Quantidade de dinheiro >>>>>>>>>>>>" + qntdDinheiro)}
                    </div>
                    {erro && <div style={{ color: 'red', marginBottom: 16 }}>{erro}</div>}
                    <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', alignItems: 'center' }}>
                        <button type="submit" id="btn_cancelar">Cancelar</button>
                        <button type="submit" id="btn_cadastrar">Cadastrar</button>
                    </div>
                </form>
            </div>
        </div>
    );
};
