import { useState, useEffect } from 'react';
import '../estilizacao/TelaJogo.css';
import {enviandoCaixaEscolhida} from '../utils/api'
import { useParams } from 'react-router-dom';
import diamante from '../imagens/diamante.png'
import imgBomba from '../imagens/bomba.png'

export default function TelaJogo() {

    const { id } = useParams();
    const [caixa_escolhida, setCaixa_escolhida] = useState("");
    const [dimaOuBomba, setDimaOuBomba] = useState("");
    const [listaDiamantes, setListaDiamantes] = useState([]);
    const [bombaMinas, setBomba] = useState(0);

    async function enviandoCaixaEscolhidaPeloUsuario(index) {
        setCaixa_escolhida(index);
        try {
            const resp = await enviandoCaixaEscolhida(index, id);
            if (resp === "DIAMANTE") {
                setListaDiamantes([...listaDiamantes, index])
            }else{
                setBomba(index)
            }
        } catch (err) {
            console.error('Erro enviando caixa escolhida:', err);
        }
    }

    useEffect(() => {
        console.log('listaDiamantes', listaDiamantes)
    },[listaDiamantes])

    useEffect(() => {
        console.log('bomba', bomba)
    },[bomba])

    return (
        <div className="tela_jogo">
            <div className="container_botoes">
                {Array.from({ length: 25 }).map((_, index) => (
                    <button
                        key={index}
                        id={`btn-${index}`}
                        value={index}
                        onClick={() => enviandoCaixaEscolhidaPeloUsuario(index)}
                    >{listaDiamantes.includes(index) ? (<img src={diamante} style={{width:30, justifyContent: 'center', alignItems: 'center', display: 'flex', marginLeft: 20}}/>) : (null)}
                    >{bombaMinas.includes(index) ? (<img src={diamante} style={{width:30, justifyContent: 'center', alignItems: 'center', display: 'flex', marginLeft: 20}}/>) : (null)}
                    </button>
                ))}
            </div>
            <button id='parar'>Parar</button>
        </div>
    );
}
